ST.xhtml:p:
(*Red FSM1_Petri*)
IF FSM1_CurrentTransition = FSM1_INICIAR 
  THEN
    FSM1_CurrentStatus:=FSM1_ARRANCANDO_BANDAS;
END_IF;

IF FSM1_CurrentTransition = FSM1_CLASIFICAR_CAJAS_COLOR 
  THEN
    FSM1_CurrentStatus:=FSM1_ESPERANDO_CLASIFICACION;
END_IF;

IF FSM1_CurrentTransition = FSM1_DETECTAR_PIEZA_EMPUJAR
    THEN
      FSM1_CurrentStatus:=FSM1_ESPERANDO_DETECCION_PIEZA;
END_IF;

IF FSM1_CurrentTransition = FSM1_CLAMPEAR
    THEN
      FSM1_CurrentStatus:=FSM1_CLAMPEANDO;
END_IF;

IF FSM1_CurrentTransition = FSM1_RECOGER_PIEZA_SUCCIONADOR1
  THEN
      FSM1_CurrentStatus:=FSM1_RECOGIENDO_PIEZA_SUCCIONADOR1;
END_IF;

IF FSM1_CurrentTransition = FSM1_RECOGER_CAJA_SUCCIONADOR2
  THEN
      FSM1_CurrentStatus:=FSM1_RECOGIENDO_CAJA_SUCCIONADOR2;
END_IF;


IF FSM1_CurrentTransition = FSM1_VACIAR_SUCCIONADOR1
  THEN
      FSM1_CurrentStatus:=FSM1_VACIANDO_SUCCIONADOR1;
END_IF;

IF FSM1_CurrentTransition = FSM1_REINICIAR
  THEN
      FSM1_CurrentStatus:=FSM1_ESPERA;
END_IF;

FSM1_FTrigStop(
  CLK := FSM1_BtnStop
);

IF FSM1_FTrigStop.Q THEN
  FSM1_CurrentTransition := FSM1_DESHABILITADO;
  FSM1_CurrentStatus := FSM1_ESPERA;
  SensorVision1Value := 0;
  FSM1_RCSorting1 := FALSE;
  FSM1_RCSorting2 := FALSE;
  FSM1_RCSorting3 := FALSE;
  FSM1_RCSorting4 := FALSE;
  FSM1_RCSorting5 := FALSE;
  FSM1_RCSorting6 := FALSE;
  FSM1_RCSorting7 := FALSE;
  FSM1_RCSorting8 := FALSE;
  FSM1_RCSorting9 := FALSE;
  FSM1_RCGreen := FALSE;
  FSM1_RCBlue := FALSE;
  FSM1_OutSucker1Grab := FALSE;
  FSM1_Sucker1Z := 0;
  FSM1_Sucker1Y := 0;
  FSM1_Sucker1X := 0;
  FSM_Sucker1 := 0;
  FSM_Sucker2 := 0;
  FSM_Sucker1B:=0;
  FSM1_OutSucker2Z := FALSE;
  FSM1_OutSucker2X := FALSE;
  FSM1_OutSucker2Grab := FALSE;
  FSM1_COMPLETE:=FALSE;
  FSM1_RESET:=FALSE;
END_IF;

CASE FSM1_CurrentStatus OF

  FSM1_ESPERA:
    IF (FSM1_BtnStart AND NOT FSM1_COMPLETE )  THEN
      FSM1_CurrentTransition := FSM1_INICIAR;
    END_IF;
    IF (FSM1_RESET AND NOT FSM1_COMPLETE )  THEN
      FSM1_RESET:=FALSE;
      FSM1_CurrentTransition := FSM1_INICIAR;
    END_IF;


  FSM1_ARRANCANDO_BANDAS:
    FSM1_RCSorting1 := TRUE;
    FSM1_RCSorting2 := TRUE;
    FSM1_RCSorting3 := TRUE;
    FSM1_RCSorting4 := TRUE;
    FSM1_RCSorting5 := TRUE;
    FSM1_RCSorting6 := TRUE;
    FSM1_RCSorting7 := TRUE;
    FSM1_RCSorting8 := TRUE;
    FSM1_RCSorting9 := TRUE;
    FSM1_RCGreen := TRUE;
    FSM1_RCBlue := TRUE;
    SensorVision1Value := 0;
    FSM1_CurrentTransition := FSM1_CLASIFICAR_CAJAS_COLOR;

  FSM1_ESPERANDO_CLASIFICACION:
    IF FSM1_SensorVision1 > 0 THEN
      SensorVision1Value := FSM1_SensorVision1;
      FSM1_CurrentTransition := FSM1_DETECTAR_PIEZA_EMPUJAR;
    END_IF;

  FSM1_ESPERANDO_DETECCION_PIEZA:
    IF SensorVision1Value = LID_GREEN OR SensorVision1Value = BASE_GREEN THEN
      TP_Pusher1(IN := FSM1_SensorPusher1, PT := T#1s, Q => FSM1_PusherSorting1);
      FTRIG_Pusher1(CLK := TP_Pusher1.Q);
      IF FTRIG_Pusher1.Q THEN
        FSM1_CurrentTransition := FSM1_CLAMPEAR;
      END_IF;
    END_IF;

    IF SensorVision1Value = LID_BLUE OR SensorVision1Value = BASE_BLUE THEN
      TP_Pusher2(IN := FSM1_SensorPusher2, PT := T#1s, Q => FSM1_PusherSorting2);
      FTRIG_Pusher2(CLK := TP_Pusher2.Q);
      IF FTRIG_Pusher2.Q THEN
        FSM1_CurrentTransition := FSM1_CLAMPEAR;
      END_IF;
    END_IF;

  FSM1_CLAMPEANDO:
    IF SensorVision1Value = LID_BLUE OR SensorVision1Value = BASE_BLUE THEN
      FTRIG_SensorClampBlue(CLK := FSM1_SensorClampBlue);
      TP_ClampBlue(IN := FTRIG_SensorClampBlue.Q, PT := T#1s, Q => FSM1_OutClampBlue);
      FTRIG_ClampBlue(CLK := TP_ClampBlue.Q);
      IF FTRIG_ClampBlue.Q THEN
        FSM1_CurrentTransition := FSM1_RECOGER_PIEZA_SUCCIONADOR1;
      END_IF;
    END_IF;

    IF SensorVision1Value = LID_GREEN OR SensorVision1Value = BASE_GREEN THEN
      FTRIG_SensorClampGreen(CLK := FSM1_SensorClampGreen);
      TP_ClampGreen(IN := FTRIG_SensorClampGreen.Q, PT := T#1s, Q => FSM1_OutClampGreen);
      FTRIG_ClampGreen(CLK := TP_ClampGreen.Q);
      IF FTRIG_ClampGreen.Q THEN
        FSM1_CurrentTransition := FSM1_RECOGER_PIEZA_SUCCIONADOR1;
      END_IF;
    END_IF;

  FSM1_RECOGIENDO_PIEZA_SUCCIONADOR1:
    IF SensorVision1Value = LID_BLUE OR SensorVision1Value = BASE_BLUE THEN
      CASE FSM_Sucker1 OF
        0:
          FSM1_Sucker1Z := 8;
          TON_Sucker1(IN := TRUE, PT := T#1s);
          IF TON_Sucker1.Q THEN
            TON_Sucker1(IN := FALSE);
            FSM_Sucker1 := 1;
          END_IF;
        1:
          FSM1_Sucker1X := 1;
          TON_Sucker1(IN := TRUE, PT := T#1s);
          IF TON_Sucker1.Q THEN
            TON_Sucker1(IN := FALSE);
            FSM_Sucker1 := 2;
          END_IF;
        2:
          FSM1_OutSucker1Grab := TRUE;
          TON_Sucker1(IN := TRUE, PT := T#1s);
          IF TON_Sucker1.Q THEN
            TON_Sucker1(IN := FALSE);
            FSM1_Sucker1Z := 0;  
            FSM_Sucker1 := 3;
          END_IF;         
        3:
          FSM_Sucker1:=0;
          FSM1_CurrentTransition := FSM1_RECOGER_CAJA_SUCCIONADOR2;
      END_CASE;
    END_IF;

    IF SensorVision1Value = LID_GREEN OR SensorVision1Value = BASE_GREEN THEN
      CASE FSM_Sucker1 OF
        0:
          FSM1_Sucker1X := 8;
          TON_Sucker1(IN := TRUE, PT := T#1s);
          IF TON_Sucker1.Q THEN
            TON_Sucker1(IN := FALSE);
            FSM_Sucker1 := 1;
          END_IF;
        1:
          FSM1_Sucker1Z := 8;
          TON_Sucker1(IN := TRUE, PT := T#1s);
          IF TON_Sucker1.Q THEN
            TON_Sucker1(IN := FALSE);
            FSM_Sucker1 := 2;
          END_IF;
        2:
          FSM1_OutSucker1Grab := TRUE;TON_Sucker1(IN := TRUE, PT := T#1s);
          IF TON_Sucker1.Q THEN
            TON_Sucker1(IN := FALSE);
            FSM1_Sucker1Z := 0;  
            FSM_Sucker1 := 3;
          END_IF;        
        3:
          FSM_Sucker1:=0;
          FSM1_CurrentTransition := FSM1_RECOGER_CAJA_SUCCIONADOR2;
      END_CASE;
    END_IF;

  FSM1_RECOGIENDO_CAJA_SUCCIONADOR2:
     CASE FSM_Sucker2 OF
      0:
        FSM1_OutRCStackableBox:=TRUE;
        FSM_Sucker2:=1;
      1:
        IF FSM1_SensorStackableBox THEN
            FSM1_OutRCStackableBox:=FALSE;
            FSM_Sucker2:=2;
        END_IF;
      2:
        FSM1_OutSucker2X:=TRUE;
        TON_Sucker2(IN := TRUE, PT := T#1s);
        IF TON_Sucker2.Q THEN
          TON_Sucker2(IN:= FALSE);
          FSM1_OutSucker2Z:=TRUE;
          FSM_Sucker2:=3;
        END_IF;

      3:(*Succión*)
        TON_Sucker2(IN := TRUE, PT := T#1s);
        IF TON_Sucker2.Q THEN
          TON_Sucker2(IN:= FALSE);
          FSM1_OutSucker2Grab:=TRUE;
          FSM1_OutSucker2X:=FALSE;
          FSM1_OutRCPalletEntry:=TRUE;
          FSM1_OutRCPalletExit:=TRUE;
          FSM_Sucker2:=4;
        END_IF;
      4:
        IF FSM1_SensorPallet THEN
              FSM1_OutRCPalletEntry:=FALSE;
              FSM1_OutRCPalletExit:=FALSE;
              FSM_Sucker2:=5;
        END_IF;
      5:
        TON_Sucker2(IN := TRUE, PT := T#1s);
        IF TON_Sucker2.Q THEN
          TON_Sucker2(IN := FALSE);
          FSM1_OutSucker2Grab:=FALSE;
          FSM1_OutSucker2Z:=FALSE;
          FSM_Sucker2:=6;
        END_IF;
      6:
        TON_Sucker2(IN:= TRUE, PT:= T#1s);
        IF TON_Sucker2.Q THEN
          TON_Sucker2(IN := FALSE);
          FSM1_CurrentTransition:=FSM1_VACIAR_SUCCIONADOR1;
          FSM_Sucker2:=0;
        END_IF;
      END_CASE;

  FSM1_VACIANDO_SUCCIONADOR1:
    CASE FSM_Sucker1B OF
      0:
        FSM1_OutRCPalletExit:=TRUE;
        IF FSM1_SensorPalletWithBox THEN
          FSM1_OutRCPalletExit:= FALSE;
          FSM_Sucker1B:=1;
        END_IF;
      1:
        FSM1_Sucker1Z:=0;
        TON_Sucker1(IN:= TRUE, PT:= T#1s);
        IF TON_Sucker1.Q THEN
          TON_Sucker1(IN:=FALSE);
          FSM1_Sucker1Y:=7;
          FSM1_Sucker1X:=5;
          FSM_Sucker1B:=2;
        END_IF;
      2:
        TON_Sucker1(IN:= TRUE, PT:= T#1s);
        IF TON_Sucker1.Q THEN
          TON_Sucker1(IN:= FALSE);
          FSM1_Sucker1Z:=10;
          FSM1_OutSucker1Grab:=FALSE;
          FSM_Sucker1B:=3;
        END_IF;    
      3:
        TON_Sucker1(IN:= TRUE, PT:= T#1s);
        IF TON_Sucker1.Q THEN
          TON_Sucker1(IN:=FALSE);
          FSM1_Sucker1Z:=0;
          FSM_Sucker1B:=4;
        END_IF;
      4:
        TON_Sucker1(IN:= TRUE, PT:= T#1s);
        IF TON_Sucker1.Q THEN
          TON_Sucker1(IN:=FALSE);
          FSM1_Sucker1X:=0;
          FSM1_Sucker1Y:=0;
          FSM1_COMPLETE:=TRUE;
          FSM1_RESET:=TRUE;
          FSM_Sucker1B:=0;
          FSM1_CurrentTransition:=FSM1_REINICIAR;
        END_IF;

    END_CASE;
END_CASE;

(*Red FSM1_Petri*)
IF FSM2_CurrentTransition = FSM2_INICIAR_SECUENCIA
  THEN
    FSM2_CurrentStatus:=FSM2_INICIANDO_SECUENCIA;
END_IF;

IF FSM2_CurrentTransition = FSM2_DETECTAR_PALLET
  THEN
    FSM2_CurrentStatus:=FSM2_DETECTANDO_PALLET;
END_IF;

IF FSM2_CurrentTransition = FSM2_UBICAR_PALLET
  THEN
    FSM2_CurrentStatus:=FSM2_UBICANDO_PALLET;
END_IF;

IF FSM2_CurrentTransition = FSM2_DEPOSITAR_PALLET
  THEN
    FSM2_CurrentStatus:= FSM2_DEPOSITANDO_PALLET;
END_IF;

FSM2_FTrigStop(
  CLK := FSM2_BtnStop
);

IF FSM2_FTrigStop.Q THEN
  FSM2_CurrentTransition := FSM2_DESHABILITADO;
  FSM2_CurrentStatus := FSM2_VALIDANDO_TRANSACCION;
  FSM2_OutRCTurn:=FALSE;
  FSM2_OutRCTurnLeft:=FALSE;
  FSM2_OutRC1:=FALSE;
  FSM2_OutRC2:=FALSE;
  FSM2_OutLoading:=FALSE;
  FSM_Locate:=0;
  FSM2_Stacker1Position:=55;
  FSM2_OutStacker1Right:=FALSE;
  FSM2_OutStacker1Lift:=FALSE;
  FSM2_OutStacker1Left:=FALSE;
END_IF;

CASE FSM2_CurrentStatus OF
  FSM2_VALIDANDO_TRANSACCION:
    IF FSM1_COMPLETE AND FSM2_SORTING_PRIORITY THEN
      FSM2_CurrentTransaction:= SORTING_TRANSACTION;
      FSM2_CurrentTransition:= FSM2_INICIAR_SECUENCIA;
    ELSIF FSM3_COMPLETE THEN
      FSM2_CurrentTransaction:= PACKAGE_TRANSACTION;
      FSM2_CurrentTransition:= FSM2_INICIAR_SECUENCIA;
    ELSIF NOT FSM3_COMPLETE AND NOT FSM2_SORTING_PRIORITY THEN
      FSM2_SORTING_PRIORITY:=TRUE;
    END_IF;

  FSM2_INICIANDO_SECUENCIA:
    IF FSM2_CurrentTransaction = SORTING_TRANSACTION THEN
          FSM2_OutRCTurn:=FALSE;
          FSM2_OutRCTurnLeft:=TRUE;
          FSM2_OutRC1:=TRUE;
          FSM2_OutRC2:=TRUE;
          FSM2_OutLoading:=TRUE;
          FSM1_OutRCPalletExit:=TRUE;
          FTRIG_SensorComplete( CLK:= FSM1_SensorComplete);
          IF FTRIG_SensorComplete.Q  THEN
              FSM1_OutRCPalletExit:=FALSE;
              FSM1_COMPLETE:=FALSE;
            FSM2_CurrentTransition:=FSM2_DETECTAR_PALLET;
          END_IF;
    END_IF;

    FSM2_DETECTANDO_PALLET:
      IF FSM2_SensorEntry THEN
        FSM2_OutRCTurn:=FALSE;
        FSM2_OutRCTurnLeft:=FALSE;
        FSM2_OutRC1:=FALSE;
        FSM2_OutRC2:=FALSE;
        FSM2_OutLoading:=FALSE;
        FSM2_CurrentTransition:=FSM2_UBICAR_PALLET;
      END_IF;      

    FSM2_UBICANDO_PALLET:
      CASE FSM_Locate OF
        0: (*Detectar Caja*)
          IF FSM2_SensorVision1 >0  THEN
              FSM2_SensorVision1Value:=FSM2_SensorVision1;
              FSM_Locate:=1;
          END_IF;
        1: (*Estirar Brazo y levantar*)
          FSM2_OutStacker1Right:=TRUE;
          TON_Locate(IN:= TRUE, PT:=T#2s);
          IF TON_Locate.Q THEN
            TON_Locate(IN:= FALSE);
            FSM2_OutStacker1Lift:=TRUE;
            FSM_Locate:=2;
          END_IF;
        2: (*Retraer brazo*)
          TON_Locate(IN:= TRUE, PT:=T#1s);
          IF TON_Locate.Q THEN
            TON_Locate(IN:=FALSE);
            FSM2_OutStacker1Right:=FALSE;
            FSM_Locate:= 3;
          END_IF;
        3:(*Ubicar pallet en columna 1 || 2*)
          IF FSM2_SensorVision1Value = BASE_GREEN OR FSM2_SensorVision1Value = LID_GREEN THEN
            FSM2_Stacker1Position:=2;
          ELSIF FSM2_SensorVision1Value = BASE_BLUE OR FSM2_SensorVision1Value = LID_BLUE THEN
            FSM2_Stacker1Position:=8;
          END_IF;
          FSM_Locate:=4;
        4: (*Esperar que llegue a ubicación*)
          FTRIG_SensorStacker1(CLK := FSM2_SensorStacker1);
          IF FTRIG_SensorStacker1.Q  THEN
              FSM2_OutStacker1Left:=TRUE;
              FSM_Locate:=5;
          END_IF;
        5:(*Bajar Pallet*)
          TON_Locate(IN:= TRUE, PT:=T#1s);
          IF TON_Locate.Q THEN
            TON_Locate(IN:=FALSE);
            FSM2_OutStacker1Lift:=FALSE;
            FSM_Locate:=6;
          END_IF;
        6:(*Retraer brazo*)
          TON_Locate(IN:= TRUE, PT:=T#1s);
          IF TON_Locate.Q THEN
            FSM2_OutStacker1Left:=FALSE;
            FSM_Locate:=7;
          END_IF;
        7:(*Reubicar en posicion inicial*)
          TON_Locate(IN:= TRUE, PT:=T#2s);
          IF TON_Locate.Q THEN
            FSM2_Stacker1Position:=55;
            FSM_Locate:=8;
          END_IF;
        8:(*Esperando que llegue a la posicion*)
          FTRIG_SensorStacker1(CLK := FSM2_SensorStacker1);
          IF FTRIG_SensorStacker1.Q THEN
              FSM_Locate:=0;
              FSM2_CurrentTransition:=FSM2_DEPOSITAR_PALLET;
          END_IF;
        END_CASE;


END_CASE;
